// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "./CIP36.sol";
import "./NetworkRegistry.sol";
import "./UnderwriteManager.sol";
import "@openzeppelin/contracts-upgradeable/utils/ContextUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";

contract RUSDV2 is CIP36 {
    /*
     *  REGISTERED: _to and _from must be registered wallets.
     *  POSITIVE: _from must have a positive balance if _to is not a registered wallet.
     *  NONE: no restrictions to _to and _from
     */
    enum Restriction { REGISTERED, POSITIVE, NONE }

    /*
     *  Events
     */
    event RestrictionUpdated(Restriction indexed state);
    event RestrictionExpirationUpdated(uint256 restrictionRenewal);
    event BalanceUpdate(
        address sender, 
        address recipient, 
        uint256 senderBalance, 
        uint256 senderCreditBalance, 
        uint256 recipientBalance, 
        uint256 recipientCreditBalance);

    event BulkBalanceUpdate(
        address sender, 
        address[] recipients, 
        uint256 senderBalance, 
        uint256 senderCreditBalance, 
        uint256[] recipientBalances, 
        uint256[] recipientCreditBalances);

    /*
     *  Storage
     */
    NetworkRegistry public registry;
    UnderwriteManager public underwriteManager;
    address public operator;

    Restriction public restrictionState;
    uint256 restrictionRenewal;
    uint256 expirationSeconds;


    modifier onlyAuthorized() override {
        require(msg.sender == address(underwriteManager) || msg.sender == owner() || msg.sender == operator, "Unauthorized caller");
        _;
    }

    function initializeRUSD(
        address registryAddress,
        uint256 _expiration,
        address _underwriteManager,
        address operatorAddress
    ) external virtual initializer {
        CIP36.initialize("rUSD", "rUSD");
        registry = NetworkRegistry(registryAddress);
        underwriteManager = UnderwriteManager(_underwriteManager);
        operator = operatorAddress;
        restrictionState = Restriction.REGISTERED;
        restrictionRenewal = block.timestamp;
        expirationSeconds = _expiration;
    }

    /*
     *  Overrides
     */
    function _transfer(
        address _from,
        address _to,
        uint256 _amount
    ) internal override {
        _verifyNetworkRegistry(_from, _to, _amount);
        super._transfer(_from, _to, _amount);
        underwriteManager.updateReward(_from, _amount);
        emit BalanceUpdate(
            _from,
            _to, 
            balanceOf(_from), 
            super.creditBalanceOf(_from),
            balanceOf(_to),
            super.creditBalanceOf(_to));
    }

    function bulkTransfer(address[] memory _to, uint256[] memory _values) public  
    {
        require(_to.length == _values.length);
        for (uint256 i = 0; i < _to.length; i++) {
            _transfer(msg.sender, _to[i], _values[i]);
        }
    }

    function setCreditLimit(address _member, uint256 _limit) public override onlyAuthorized() {
        super.setCreditLimit(_member, _limit);
    }

    function _verifyNetworkRegistry(
        address _from,
        address _to,
        uint256 _amount
    ) private view {
        if (restrictionState == Restriction.NONE) {
            return;
        }
        if (restrictionState == Restriction.REGISTERED) {
            require(registry.isMember(_from), "Sender is not network member");
            require(registry.isMember(_to), "Recipient is not network member");
        }
        // if in positive restriction state, recipient is not in the registry and the sender's balance is negative
        if (restrictionState == Restriction.POSITIVE && !registry.isMember(_to)) {
            uint256 _balanceFrom = super.balanceOf(_from);
            require(_balanceFrom - _amount >= 0, "Insufficient balance for non network member");
        }
    }

    function restrictRegistered() external onlyOwner() {
        if (restrictionState == Restriction.REGISTERED || restrictionState == Restriction.NONE) {
            return;
        }
        emit RestrictionUpdated(Restriction.REGISTERED);
        restrictionState = Restriction.REGISTERED;
    }

    function restrictPositiveBalance() external onlyOwner() {
        if (restrictionState == Restriction.POSITIVE || restrictionState == Restriction.NONE) {
            return;
        }
        emit RestrictionUpdated(Restriction.POSITIVE);
        restrictionState = Restriction.POSITIVE;
    }

    function removeRestrictions() external {
        if (restrictionState == Restriction.NONE) {
            revert("Already non restrictive");
        }
        if ((block.timestamp - restrictionRenewal) < expirationSeconds) {
            revert("Restriction state not expired...");
        }
        emit RestrictionUpdated(Restriction.NONE);
        restrictionState = Restriction.NONE;
    }

    function updateRestrictionExpiration() external onlyOwner() {
        emit RestrictionExpirationUpdated(block.timestamp);
        restrictionRenewal = block.timestamp;
    }
}

contracts/CIP36/NetworkRegistry.sol.content:
// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "../iKeyWallet/IiKeyWalletDeployer.sol";

/// @title NetworkRegistry - Allows Network Members to be added and removed by Network Operators.
/// @author Bridger Zoske - <bridger@resourcenetwork.co>
contract NetworkRegistry is OwnableUpgradeable  {
    /*
     *  Events
     */
    event MemberAddition(address[] indexed member);
    event MemberRemoval(address indexed member);
    event OperatorAddition(address indexed operator);
    event OperatorRemoval(address indexed operator);
    event WalletDeployed(address newMember);

    /*
     *  Storage
     */
    mapping(address => bool) public isMember;
    mapping(address => bool) public isOperator;
    address[] public operators;
    address[] public members;
    address walletDeployer;

    /*
     *  Modifiers
     */
    modifier onlyOperator(address operator) {
        require(isOperator[operator], "address is not operator");
        _;
    }

    modifier memberDoesNotExist(address member) {
        require(!isMember[member], "member already exists");
        _;
    }

    modifier memberExists(address member) {
        require(isMember[member], "member does not exist");
        _;
    }

    modifier operatorDoesNotExist(address operator) {
        require(!isOperator[operator], "operator already exists");
        _;
    }

    modifier operatorExists(address operator) {
        require(isOperator[operator], "operator does not exist");
        _;
    }

    modifier notNull(address _address) {
        require(_address != address(0), "invalid operator address");
        _;
    }

    /*
     * External functions
     */
    /// @dev Contract initialzer sets initial members and initial operators.
    /// @param _members List of initial members.
    /// @param _operators List of initial operators.
    function initialize(address[] memory _members, address[] memory _operators, address _walletDeployer) external virtual initializer {
        __Ownable_init();
        for (uint256 i = 0; i < _members.length; i++) {
            require(!isMember[_members[i]] && _members[i] != address(0));
            isMember[_members[i]] = true;
        }
        for (uint256 j = 0; j < _operators.length; j++) {
            require(!isOperator[_operators[j]] && _operators[j] != address(0));
            isOperator[_operators[j]] = true;
        }
        members = _members;
        operators = _operators;
        operators.push(owner());
        walletDeployer = _walletDeployer;
        isOperator[owner()] = true;
    }

    /// @dev Allows operator to add a new member. Transaction has to be sent by an operator wallet.
    /// @param _members Addresses of new members.
    function addMembers(address[] memory _members) external onlyOperator(msg.sender) {
        for (uint256 i = 0; i < _members.length; i++) {
            require(!isMember[_members[i]] && _members[i] != address(0));
            isMember[_members[i]] = true;
            members.push(_members[i]);
        }
        emit MemberAddition(_members);
    }

    /// @dev Allows to remove a member. Transaction has to be sent by operator.
    /// @param member Address of member.
    function removeMember(address member) external onlyOperator(msg.sender) memberExists(member) {
        isMember[member] = false;
        for (uint256 i = 0; i < members.length - 1; i++)
            if (members[i] == member) {
                members[i] = members[members.length - 1];
                break;
            }
        members.pop();
        emit MemberRemoval(member);
    }

    /// @dev Allows to add a new operator. Transaction has to be sent by an operator wallet.
    /// @param operator Address of new operator.
    function addOperator(address operator)
        external
        onlyOperator(msg.sender)
        operatorDoesNotExist(operator)
        notNull(operator)
    {
        isOperator[operator] = true;
        operators.push(operator);
        emit OperatorAddition(operator);
    }

    /// @dev Allows to remove a operator. Transaction has to be sent by operator.
    /// @param operator Address of operator.
    function removeOperator(address operator) external onlyOperator(msg.sender) operatorExists(operator) {
        require(operator != owner(), "can't remove owner operator");
        isOperator[operator] = false;
        for (uint256 i = 0; i < operators.length - 1; i++)
            if (operators[i] == operator) {
                operators[i] = operators[operators.length - 1];
                break;
            }
        operators.pop();
        emit OperatorRemoval(operator);
    }

    function deployNewWallet(
        address[] memory _clients,
        address[] memory _guardians, 
        address _coSigner,
        uint256 _required) public onlyOperator(msg.sender) {
        address newWallet = IiKeyWalletDeployer(walletDeployer).deployWallet(_clients, _guardians, _coSigner, _required);
        OwnableUpgradeable(newWallet).transferOwnership(owner());
        isMember[newWallet] = true;
        members.push(newWallet);
        emit WalletDeployed(newWallet);
    }

    /*
     * Web3 call functions
     */
    /// @dev Returns list of members.
    /// @return List of member addresses.
    function getMembers() external view returns (address[] memory) {
        return members;
    }

    /// @dev Returns list of operators.
    /// @return List of operator addresses.
    function getOperators() external view returns (address[] memory) {
        return operators;
    }
}

contracts/CIP36/UnderwriteManager.sol.content:
pragma solidity ^0.8.0;

import "./CIP36.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";



contract UnderwriteManager is OwnableUpgradeable {
    /*
     *  Constants
     */
    uint256 public constant MU_PRICE_DENOMINATOR_USD = 5;
    uint256 public constant LEVERAGE_DENOMINATOR = 5;
    uint256 public constant MWEI = 1000000000000;
    uint256 public constant REWARD_PERCENT = 2;
    uint256 public constant CREDIT_RENEWAL = 180 days;
    uint256 public constant MINIMUM_COLLATERAL = 600 ether;

    /*
     *  Storage
     */
    IERC20 public collateralToken;

    // underwriter => underwritee => credit line
    mapping(address => mapping(address => CreditLine)) public creditLines;
    mapping(address => bool) private networks;
    mapping(address => address) public underwriters;
    bool public isActive;
    uint256 public totalCollateral;

    struct CreditLine {
        uint256 collateral;
        address networkToken;
        uint256 issueDate;
        uint256 reward;
    }

    struct CreditLineEvent {
        address underwriter;
        address underwritee;
        CreditLine data;
    }

    struct CreditLineLimitEvent {
        address underwriter;
        address underwritee;
        CreditLine data;
        uint256 creditLimit;
    }

    /*
     *  Events
     */
    event NewCreditLine(CreditLineLimitEvent creditLine);
    event ExtendCreditLine(
        CreditLineLimitEvent creditLine, 
        uint256 additionalCollateral
    );
    event CreditLineReward(CreditLineEvent creditLine);
    event CreditLineRewardClaimed(
        address underwriter,
        address[] underwritees,
        uint256[] rewards,
        uint256 totalClaimed
    );
    event CreditLineWithdrawal(CreditLineLimitEvent creditLine);

    function initialize(address _collateralTokenAddress) external virtual initializer {
        collateralToken = IERC20(_collateralTokenAddress);
        isActive = true;
        __Ownable_init();
    }

    /*
     *  Modifiers
     */
    modifier notNull(address _address) {
        require(_address != address(0), "Invalid address");
        _;
    }

    modifier onlyNetwork(address _address) {
        require(networks[_address] == true || _address == owner(), "Invalid network address");
        _;
    }

    modifier validCreditRenewal(address underwriter, address underwritee) {
        uint256 issueDate = creditLines[underwriter][underwritee].issueDate;
        require(block.timestamp - issueDate > CREDIT_RENEWAL, "Credit limit still active");
        _;
    }

    modifier newCreditLine(address underwritee) {
        require(underwriters[underwritee] == address(0), "Address already underwritten");
        _;
    }

    modifier ownedCreditLine(address underwriter, address underwritee) {
        require(underwriters[underwritee] == underwriter, "Credit line not owned");
        _;
    }

    modifier active() {
        require(isActive == true, "Manager is inactive");
        _;
    }

    /*
     * Public functions
     */
    function underwrite(
        address networkToken,
        uint256 collateralAmount,
        address underwritee
    ) newCreditLine(underwritee) active notNull(networkToken) notNull(underwritee) external {
        CreditLine storage creditLine = creditLines[msg.sender][underwritee];
        require(creditLine.collateral == 0, "Credit line already underwritten");
        require(collateralAmount >= MINIMUM_COLLATERAL, "Insufficient collateral");
        networks[networkToken] = true;
        collateralToken.transferFrom(msg.sender, address(this), collateralAmount);
        creditLine.collateral = collateralAmount;
        creditLine.networkToken = networkToken;
        creditLine.issueDate = block.timestamp;
        uint256 creditLimit = calculateCredit(collateralAmount);
        emit NewCreditLine(CreditLineLimitEvent(
            msg.sender, 
            underwritee, 
            CreditLine(
                creditLine.collateral, 
                creditLine.networkToken, 
                creditLine.issueDate,
                0
            ),
            creditLimit
        ));
        CIP36(networkToken).setCreditLimit(underwritee, creditLimit); 
        totalCollateral += collateralAmount;
        underwriters[underwritee] = msg.sender;
    }

    function extendCreditLine(address underwritee, uint256 collateralAmount) external active ownedCreditLine(msg.sender, underwritee) {
        CreditLine storage creditLine = creditLines[msg.sender][underwritee];
        require(creditLine.collateral >= MINIMUM_COLLATERAL, "Credit line not underwritten");
        collateralToken.transferFrom(msg.sender, address(this), collateralAmount);
        creditLine.collateral += collateralAmount;
        uint256 creditLimit = calculateCredit(creditLine.collateral);
        CIP36(creditLine.networkToken).setCreditLimit(underwritee, creditLimit);
        totalCollateral += collateralAmount;
        emit ExtendCreditLine(CreditLineLimitEvent(
            msg.sender, 
            underwritee, 
            CreditLine(
                creditLine.collateral, 
                creditLine.networkToken, 
                creditLine.issueDate,
                0
            ),
            creditLimit
        ), collateralAmount);
    }

    function withdraw(address underwritee) external validCreditRenewal(msg.sender, underwritee) notNull(underwritee) {
        CreditLine memory creditLine = creditLines[msg.sender][underwritee];
        uint256 collateral = creditLine.collateral;
        require (collateral > 0, "Can't withdraw from empty credit line");
        uint256 creditBalance = CIP36(creditLine.networkToken).creditBalanceOf(underwritee);
        uint256 offsetBalance = creditBalance * MWEI;
        uint256 total = creditLine.collateral + creditLine.reward - offsetBalance;
        require( offsetBalance > 0, "Can't withdraw from active credit line");
        CIP36(creditLine.networkToken).setCreditLimit(underwritee, 0);
        collateralToken.transfer(msg.sender, total);
        creditLines[msg.sender][underwritee] = CreditLine(0, address(0), 0, 0);
        underwriters[underwritee] = address(0);
        totalCollateral -= collateral;
        emit CreditLineWithdrawal(CreditLineLimitEvent(
            msg.sender, 
            underwritee, 
            CreditLine(
                creditLine.collateral, 
                creditLine.networkToken, 
                creditLine.issueDate,
                0
            ),
            0
        ));
    }

    function renewCreditLine(address underwritee) external validCreditRenewal(msg.sender, underwritee) {
        CreditLine storage creditLine = creditLines[msg.sender][underwritee];
        require(underwriters[underwritee] == msg.sender, "Must be underwriter to renew credit line");
        creditLine.issueDate = block.timestamp;
    }

    function updateReward(address underwritee, uint256 txAmount) external notNull(underwritee) onlyNetwork(msg.sender) {
        address underwriter = underwriters[underwritee];
        if (underwriter == address(0)) {
            return;
        }
        CreditLine storage creditLine = creditLines[underwriter][underwritee];
        if (creditLine.collateral == 0) {
            return;
        }
        tryUpdateCreditLine(underwritee, underwriter);
        uint256 reward = calculateReward(txAmount);
        creditLine.reward += reward;
        emit CreditLineReward(CreditLineEvent(
            underwriter, 
            underwritee, 
            CreditLine(
                creditLine.collateral, 
                creditLine.networkToken, 
                creditLine.issueDate,
                reward
            )
        ));
    }

    function claimRewards(address[] memory underwritees) external {
        uint256 totalReward = 0;
        uint256[] memory rewards = new uint256[](underwritees.length);
        for (uint256 i = 0; i < underwritees.length; i++) {
            CreditLine storage creditLine = creditLines[msg.sender][underwritees[i]];
            if (creditLine.reward > 0) {
                rewards[i] = creditLine.reward;
                totalReward += creditLine.reward;
                creditLine.reward = 0;
            }
        }
        require(totalReward > 0, "No reward to claim");
        require(collateralToken.balanceOf(address(this)) - totalReward > totalCollateral, "Insufficient funds in reward pool");
        collateralToken.transfer(msg.sender, totalReward);
        emit CreditLineRewardClaimed(msg.sender, underwritees, rewards, totalReward);
    }

    function toggleActive() external onlyOwner() {
        isActive = !isActive;
    }

    function addNetwork(address networkAddress) external onlyOwner() {
        networks[networkAddress] = true;
    }

    function removeNetwork(address networkAddress) external onlyOwner() {
        networks[networkAddress] = false;
    }

    // Returns calculation in mwei units
    function calculateCredit(uint256 collateralAmount) public pure returns (uint256) {
        return ((collateralAmount / MWEI) * (LEVERAGE_DENOMINATOR)) / MU_PRICE_DENOMINATOR_USD;
    }
    // Returns calculation in ether units
    function calculateCollateral(uint256 creditAmount) public pure returns (uint256) {
        return ((creditAmount * MWEI) * (LEVERAGE_DENOMINATOR)) / MU_PRICE_DENOMINATOR_USD;
    }

    /*
     * Private functions
     */
    function calculateReward(uint256 txAmount) private pure returns (uint256) {
        return (txAmount / 100) * REWARD_PERCENT * MWEI;
    }

    function tryUpdateCreditLine(address underwritee, address underwriter) private {
        if ((block.timestamp - creditLines[underwriter][underwritee].issueDate) > CREDIT_RENEWAL + 1 days) {
            creditLines[underwriter][underwritee].issueDate = block.timestamp;
        }
    }

    /*
     * Web3 call functions
     */
    /// @dev Returns list of networks.
    /// @return List of networks.
    function isNetwork(address _address) external view returns (bool) {
        return networks[_address];
    }
}

